import { HYDRATE } from 'next-redux-wrapper';
import { IFilterReducer } from '../../interfaces/IFilterReducer';
import { filterType } from "../actionCreators";

export const INITIAL_STATE: IFilterReducer = {
    filters: {},
};

export default function reducer(state = INITIAL_STATE, action) {
    switch (action.type) {
        case HYDRATE: {
            return {
                ...state, // use previous state
                ...action.payload.filter, // apply delta from hydration
            };
        }
        case filterType.FILTER_ADD: {
            return {
                ...state,
                filters: {
                    ...state.filters,
                    [action.id]: action.data,
                }
            }
        }
        default: {
            return state;
        }
    }
}
