import { TSidebarProps } from "@props/TSidebarProps";
import React from "react";
import { connect } from "react-redux";
import { BEM } from "../libs/BEM";
import { ScrollbarComponent, SidebarComponent } from "@codeunic/library-ui/build";

export const Sidebar = (props: TSidebarProps) => {
    const bm = new BEM("sidebar", {
        "open": props.open,
    });
    bm.Append(props.className);

    return (
        <SidebarComponent style={{
            ...props.style,
            backgroundColor: "white",
        }} className={bm.toString()}>
            <ScrollbarComponent>
                Hola
            </ScrollbarComponent>
        </SidebarComponent>
    )
}

export default connect(({global}: any) => ({
    open: global.sidebar,
}))(Sidebar);
